mapper.select:
SELECT USR_SN AS usrSn , USR_ID AS usrId , PWD AS pwd , ACTV_NM AS actvNm , ROLE_CD AS roleCd , RS_PWD_YN AS rsPwdYn , TMP_PWD AS tmpPwd , REG_DATE AS regDate , REGR AS regr , MOD_DATE AS modDate , MODR AS modr FROM TB_USER
WHERE USR_ID = #{usrId}
WHERE ACTV_NM = #{actvNm}

mapper.insert:
INSERT INTO TB_USER (
	USR_ID
	, PWD
	, ACTV_NM
	, REGR
	, MODR
) VALUES (
	#{usrId}
	, #{pwd}
	, #{actvNm}
	, #{regr}
	, #{modr}
);
