mapper.select:
SELECT SERIAL AS serial , ID AS id , PASSWORD AS password , NICK_NAME AS nickName , EMAIL AS email , MEMBER_STATUS AS memberStatus , REGISTER_DATE AS registerDate , REGISTER AS register , UPDATE_DATE AS updateDate , UPDATE_USER AS updateUser , DELETED AS deleted FROM TB_USER
WHERE ID = #{id}
WHERE NICK_NAME = #{nickName}

mapper.insert:
INSERT INTO TB_USER (
	SERIAL
	, ID
	, PASSWORD
	, NICK_NAME
	, EMAIL
	, MEMBER_STATUS
) VALUES (
	#{serial}
	, #{id}
	, #{password}
	, #{nickName}
	, #{email}
	, '001'
);
